var md = require('./md');
var fs = require('fs');

process.argv.splice(0,2);

var fspec = process.argv.shift();

console.log("processing " + fspec);

var file = fs.readFileSync(fspec);

var jsDOC = file.toString().match(/\*[^*]*\*+(?:[^*/][^*]*\*+)*/gm);

var parsed = md.parse(jsDOC[0]);

console.log(parsed.toHtml());

